SELECT UG.TITLE, UG.BOARD_ID, UGR.REPLY_ID, UGR.WRITER_ID, UGR.CONTENTS, TO_CHAR(UGR.CREATED_DATE, 'YYYY-MM-DD') AS CREATED_DATE FROM USED_GOODS_BOARD UG JOIN USED_GOODS_REPLY UGR ON UG.BOARD_ID = UGR.BOARD_ID WHERE UG.CREATED_DATE BETWEEN TO_DATE('2022-10-01', 'YYYY-MM-DD')  AND TO_DATE('2022-10-31', 'YYYY-MM-DD') ORDER BY UGR.CREATED_DATE , UG.TITLE;